import React from 'react';

const teachersData = [
  {
    id: 1,
    name: "Namya Modi",
    image: "/api/placeholder/300/300", // Replace with actual image path
    specialization: "VCE Mathematics Specialist",
    subjects: ["VCE Maths Specialist", "VCE Maths Methods"],
    education: "Bachelor of Medical Science and Doctor of Medicine (MD) - Monash University",
    experience: "4 years of tutoring experience",
    description: "Hello! I'm Namya, a passionate tutor specializing in VCE Mathematics, particularly Maths Specialist and Maths Methods. Currently studying the Bachelor of Medical Science and Doctor of Medicine (MD) degree from Monash University and 4 years of tutoring experience, I am dedicated to helping students achieve their academic goals.",
    approach: "My teaching approach focuses on fostering a positive, engaging environment where students can explore concepts and build confidence. I tailor my methods to meet individual learning styles, ensuring each student grasps advanced topics like calculus, algebra, and statistics.",
    achievements: "Many of my students have achieved outstanding results in their VCE exams, and I take pride in supporting their journeys toward success.",
    contact: "For inquiries or to schedule a session, please contact me at [Your Contact Information]."
  },
  {
    id: 2,
    name: "Tawheed",
    image: "/api/placeholder/300/300", // Replace with actual image path
    specialization: "VCE Mathematics & English",
    subjects: ["VCE Mathematics (7-VCE)", "VCE English (7-VCE)", "VCE Specialist Mathematics"],
    education: "Melbourne High School Graduate - Biomedical Sciences, Monash University",
    experience: "Experienced tutor with proven track record",
    description: "Hi, I'm Tawheed, a tutor who teaches both (7-VCE) Mathematics and (7-VCE) English, with a proficiency in VCE English. I graduated from Melbourne High School, and now I'm an undergraduate student at Monash University studying Biomedical Sciences.",
    approach: "High school does take loads of effort and talent, which is my learning style that allows students of all levels to not only make learning easy but also allows them to enjoy learning. It focuses on personal strategies, how to mitigate negative attitudes, and high-level questions and answers to help your child understand the subject.",
    achievements: "My students end up surpassing their expectations in not only school but also VCE, and I take pride in having a personal understanding of how your student performs and studies.",
    contact: "For inquiries or to schedule a session, please contact MathSCILab's main office."
  }
];

export default function Teachers() {
  const styles = {
    container: {
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      padding: '2rem 0',
      fontFamily: '"Segoe UI", Tahoma, Geneva, Verdana, sans-serif'
    },
    header: {
      textAlign: 'center',
      marginBottom: '3rem',
      padding: '0 1rem'
    },
    title: {
      fontSize: '3rem',
      fontWeight: 'bold',
      color: 'white',
      marginBottom: '1rem',
      textShadow: '2px 2px 4px rgba(0,0,0,0.3)'
    },
    subtitle: {
      fontSize: '1.2rem',
      color: 'rgba(255,255,255,0.9)',
      maxWidth: '600px',
      margin: '0 auto'
    },
    teachersGrid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fit, minmax(800px, 1fr))',
      gap: '2rem',
      padding: '0 2rem',
      maxWidth: '1400px',
      margin: '0 auto'
    },
    teacherCard: {
      background: 'white',
      borderRadius: '20px',
      padding: '2rem',
      boxShadow: '0 20px 40px rgba(0,0,0,0.1)',
      transition: 'transform 0.3s ease, box-shadow 0.3s ease',
      cursor: 'pointer'
    },
    teacherCardHover: {
      transform: 'translateY(-5px)',
      boxShadow: '0 25px 50px rgba(0,0,0,0.15)'
    },
    teacherHeader: {
      display: 'flex',
      alignItems: 'center',
      marginBottom: '1.5rem',
      gap: '1.5rem'
    },
    teacherImage: {
      width: '120px',
      height: '120px',
      borderRadius: '50%',
      objectFit: 'cover',
      border: '4px solid #667eea',
      flexShrink: 0
    },
    teacherInfo: {
      flex: 1
    },
    teacherName: {
      fontSize: '2rem',
      fontWeight: 'bold',
      color: '#333',
      marginBottom: '0.5rem'
    },
    specialization: {
      fontSize: '1.1rem',
      color: '#667eea',
      fontWeight: '600',
      marginBottom: '0.5rem'
    },
    education: {
      fontSize: '0.95rem',
      color: '#666',
      marginBottom: '0.5rem'
    },
    experience: {
      fontSize: '0.9rem',
      color: '#888',
      fontStyle: 'italic'
    },
    subjectsContainer: {
      marginBottom: '1.5rem'
    },
    subjectsTitle: {
      fontSize: '1.1rem',
      fontWeight: 'bold',
      color: '#333',
      marginBottom: '0.5rem'
    },
    subjectsList: {
      display: 'flex',
      flexWrap: 'wrap',
      gap: '0.5rem'
    },
    subjectTag: {
      background: 'linear-gradient(45deg, #667eea, #764ba2)',
      color: 'white',
      padding: '0.3rem 0.8rem',
      borderRadius: '20px',
      fontSize: '0.85rem',
      fontWeight: '500'
    },
    section: {
      marginBottom: '1.5rem'
    },
    sectionTitle: {
      fontSize: '1.1rem',
      fontWeight: 'bold',
      color: '#333',
      marginBottom: '0.5rem',
      display: 'flex',
      alignItems: 'center',
      gap: '0.5rem'
    },
    sectionText: {
      fontSize: '0.95rem',
      color: '#555',
      lineHeight: '1.6',
      textAlign: 'justify'
    },
    contactSection: {
      background: 'linear-gradient(45deg, #f8f9fa, #e9ecef)',
      padding: '1rem',
      borderRadius: '10px',
      marginTop: '1rem'
    },
    contactText: {
      fontSize: '0.9rem',
      color: '#495057',
      fontStyle: 'italic'
    },
    icon: {
      fontSize: '1.2rem'
    }
  };

  const [hoveredCard, setHoveredCard] = React.useState(null);

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1 style={styles.title}>Meet Our Expert Teachers</h1>
        <p style={styles.subtitle}>
          Dedicated educators committed to helping you achieve academic excellence in VCE Mathematics and English
        </p>
      </div>

      <div style={styles.teachersGrid}>
        {teachersData.map((teacher) => (
          <div
            key={teacher.id}
            style={{
              ...styles.teacherCard,
              ...(hoveredCard === teacher.id ? styles.teacherCardHover : {})
            }}
            onMouseEnter={() => setHoveredCard(teacher.id)}
            onMouseLeave={() => setHoveredCard(null)}
          >
            <div style={styles.teacherHeader}>
              <img
                src={teacher.image}
                alt={teacher.name}
                style={styles.teacherImage}
              />
              <div style={styles.teacherInfo}>
                <h2 style={styles.teacherName}>{teacher.name}</h2>
                <p style={styles.specialization}>{teacher.specialization}</p>
                <p style={styles.education}>{teacher.education}</p>
                <p style={styles.experience}>{teacher.experience}</p>
              </div>
            </div>

            <div style={styles.subjectsContainer}>
              <h3 style={styles.subjectsTitle}>📚 Subjects Taught</h3>
              <div style={styles.subjectsList}>
                {teacher.subjects.map((subject, index) => (
                  <span key={index} style={styles.subjectTag}>
                    {subject}
                  </span>
                ))}
              </div>
            </div>

            <div style={styles.section}>
              <h3 style={styles.sectionTitle}>
                <span style={styles.icon}>👋</span> About Me
              </h3>
              <p style={styles.sectionText}>{teacher.description}</p>
            </div>

            <div style={styles.section}>
              <h3 style={styles.sectionTitle}>
                <span style={styles.icon}>🎯</span> Teaching Approach
              </h3>
              <p style={styles.sectionText}>{teacher.approach}</p>
            </div>

            <div style={styles.section}>
              <h3 style={styles.sectionTitle}>
                <span style={styles.icon}>🏆</span> Student Success
              </h3>
              <p style={styles.sectionText}>{teacher.achievements}</p>
            </div>

            <div style={styles.contactSection}>
              <h3 style={styles.sectionTitle}>
                <span style={styles.icon}>📞</span> Contact Information
              </h3>
              <p style={styles.contactText}>{teacher.contact}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
