// BookingPage.jsx
import React, { useState } from 'react';
import './Booking.css';
import courseData from './courseSchedule.json';

export default function BookingPage() {
  const [step, setStep] = useState(1);
  const [selectedLevel, setSelectedLevel] = useState('');
  const [selectedCourse, setSelectedCourse] = useState('');
  const [selectedSessions, setSelectedSessions] = useState([]);

  const nextStep = () => setStep(prev => prev + 1);
  const prevStep = () => setStep(prev => prev - 1);

  const levels = [...new Set(courseData.map(item => item.year))];

  const handleLevelChange = (e) => {
    setSelectedLevel(e.target.value);
    setSelectedCourse('');
    setSelectedSessions([]);
  };

  const handleCourseChange = (e) => {
    setSelectedCourse(e.target.value);
    setSelectedSessions([]);
  };

  const handleSelectSession = (session) => {
    setSelectedSessions([session]);
  };

  const allCourses = courseData.filter(item => item.year === selectedLevel);
  const filteredCourses = allCourses.filter(course => course.subject === selectedCourse);

  return (
    <div className="booking-container styled">
      <div className="step-indicator">
        {[1, 2, 3].map(n => (
          <div key={n} className={`step ${step === n ? 'active' : ''}`}>{n}</div>
        ))}
      </div>

      {step === 1 && (
        <div className="step-content">
          <h2>Course Booking</h2>
          <div className="form-row">
            <div>
              <label>Please select your childâ€™s current year level:</label>
              <select value={selectedLevel} onChange={handleLevelChange}>
                <option>Select...</option>
                {levels.map(level => <option key={level} value={level}>{level}</option>)}
              </select>
            </div>
            <div>
              <label>Please select course:</label>
              <select value={selectedCourse} onChange={handleCourseChange}>
                <option>Select...</option>
                {[...new Set(allCourses.map(course => course.subject))].map((subject, idx) => (
                  <option key={idx} value={subject}>{subject}</option>
                ))}
              </select>
            </div>
          </div>

          <h4>Available Sessions</h4>
          <table className="styled-table">
            <thead>
              <tr><th>Select</th><th>Course Name</th><th>Start Date</th><th>End Date</th><th>Time</th><th>Sessions</th><th>Fee</th></tr>
            </thead>
            <tbody>
              {filteredCourses.length > 0 ? (
                filteredCourses.map((course, index) => (
                  <tr key={index}>
                    <td><button onClick={() => handleSelectSession(course)}>ADD</button></td>
                    <td>{course.subject}</td>
                    <td>{course.from}</td>
                    <td>{course.to}</td>
                    <td>{course.time}</td>
                    <td>{course.sessions}</td>
                    <td>{course.fee}</td>
                  </tr>
                ))
              ) : (
                <tr><td colSpan="7">No sessions found</td></tr>
              )}
            </tbody>
          </table>

          <h4>Selected Sessions</h4>
          <table className="styled-table">
            <thead><tr><th>Course Name</th><th>Start Date</th><th>End Date</th><th>Time</th><th>Sessions</th><th>Fee</th></tr></thead>
            <tbody>
              {selectedSessions.map((s, i) => (
                <tr key={i}>
                  <td>{s.subject}</td>
                  <td>{s.from}</td>
                  <td>{s.to}</td>
                  <td>{s.time}</td>
                  <td>{s.sessions}</td>
                  <td>{s.fee}</td>
                </tr>
              ))}
            </tbody>
          </table>

          <button className="next-btn" onClick={nextStep}>NEXT</button>
        </div>
      )}

      {step === 2 && (
        <div className="step-content">
          <h2>Personal Details</h2>
          <div className="form-grid">
            <input placeholder="First Name" />
            <input placeholder="Last Name" />
            <input placeholder="Email" />
            <input placeholder="Phone" />
          </div>
          <button className="next-btn" onClick={nextStep}>NEXT</button>
        </div>
      )}

      {step === 3 && (
        <div className="step-content">
          <h2>Confirm Booking</h2>
          <label><input type="checkbox" /> I agree to the Terms and Conditions</label>
          <div className="btn-group">
            <button onClick={prevStep}>BACK</button>
            <button>SUBMIT</button>
          </div>
        </div>
      )}
    </div>
  );
}
