import { useState, useEffect } from 'react';
import './Booking.css';
import courseData from './courseSchedule.json';
import { PaymentElement, useStripe, useElements } from '@stripe/react-stripe-js';

export default function BookingPage() {
  const [step, setStep] = useState(1);
  const [selectedLevel, setSelectedLevel] = useState('');
  const [selectedCourse, setSelectedCourse] = useState('');
  const [selectedSessions, setSelectedSessions] = useState([]);
  const [formData, setFormData] = useState({ email: '' });
  const [agreed, setAgreed] = useState(false);

  const stripe = useStripe();
  const elements = useElements();

  // ✅ Step navigation
  const nextStep = () => setStep(prev => prev + 1);
  const prevStep = () => setStep(prev => prev - 1);

  // ✅ Get all unique year levels
  const levels = [...new Set(courseData.map(item => item.year))];
  const allCourses = courseData.filter(course => course.year === selectedLevel);
  const filteredCourses = allCourses.filter(course => course.subject === selectedCourse);

  // ✅ Handle level change
  const handleLevelChange = (e) => {
    setSelectedLevel(e.target.value);
    setSelectedCourse('');
    setSelectedSessions([]);
  };

  // ✅ Handle course change
  const handleCourseChange = (e) => {
    setSelectedCourse(e.target.value);
    setSelectedSessions([]);
  };

  // ✅ Select a session
  const handleSelectSession = (session) => {
    setSelectedSessions([session]);
  };

  // ✅ Handle form inputs (email, etc.)
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  // ✅ Step 3: Stripe Payment submit
  // ✅ Step 3: Stripe Payment submit
  const handleStripeSubmit = async (e) => {
    e.preventDefault();
    if (!stripe || !elements) return;

    const result = await stripe.confirmPayment({
      elements,
      confirmParams: {
        return_url: window.location.origin + '/dashboard', // redirect after success
      },
    });

    if (result.error) {
      alert(result.error.message);
    }
  };
  // ✅ Step 3: Create payment intent when sessions selected
  useEffect(() => {
    const createPaymentIntent = async () => {
      if (selectedSessions.length > 0 && formData.email) {
        const selectedSession = selectedSessions[0];
        const totalAmount = parseFloat(selectedSession.fee.replace('$', ''));

        try {
          const res = await fetch('https://your-backend-url.com/api/create-payment-intent', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
              amount: Math.round(totalAmount * 100), // cents
              email: formData.email,
              year: selectedSession.year,
              subject: selectedSession.subject
            })
          });

          const data = await res.json();
          if (data.clientSecret) {
            // ✅ Store user info locally for now (production will come from DB)
            localStorage.setItem("currentUser", JSON.stringify({
              email: formData.email,
              year: selectedSession.year,
              subject: selectedSession.subject,
              paidYear: selectedSession.year
            }));
          }
        } catch (err) {
          console.error("❌ Payment Intent Error:", err);
        }
      }
    };

    createPaymentIntent();
  }, [selectedSessions, formData.email]);

  // ✅ Calculate total, surcharge, and subtotal
  function calculateTotal() {
    if (selectedSessions.length === 0) {
      return { subtotal: 0, surcharge: 0, total: 0 };
    }
    const subtotal = selectedSessions.reduce((sum, session) => {
      const fee = parseFloat(session.fee.replace('$', ''));
      return sum + (isNaN(fee) ? 0 : fee);
    }, 0);
    const surcharge = subtotal * 0.029;
    const total = subtotal + surcharge;
    return { subtotal, surcharge, total };
  }

  return (
    <div>
      {[1, 2, 3].map(n => (
        <div key={n} className={`step ${step === n ? 'active' : ''}`}>{n}</div>
      ))}

      {/* ✅ Step 1: Course Booking */}
      {step === 1 && (
        <div className="step-content">
          <h2>Course Booking</h2>
          <div className="form-row">
            <div>
              <label>Year Level:</label>
              <select value={selectedLevel} onChange={handleLevelChange} required>
                <option>Select...</option>
                {levels.map((level, index) => (
                  <option key={index} value={level}>{level}</option>
                ))}
              </select>
            </div>
            <div>
              <label>Course:</label>
              <select value={selectedCourse} onChange={handleCourseChange} required>
                <option>Select...</option>
                {[...new Set(allCourses.map(course => course.subject))].map((subject, index) => (
                  <option key={index} value={subject}>{subject}</option>
                ))}
              </select>
            </div>
          </div>

          <h4>Available Sessions</h4>
          <table className="styled-table">
            <thead>
              <tr><th>Select</th><th>Subject</th><th>From</th><th>To</th><th>Time</th><th>Sessions</th><th>Fee</th></tr>
            </thead>
            <tbody>
              {filteredCourses.length > 0 ? filteredCourses.map((course, index) => (
                <tr key={index}>
                  <td><button onClick={() => handleSelectSession(course)}>ADD</button></td>
                  <td>{course.subject}</td>
                  <td>{course.from}</td>
                  <td>{course.to}</td>
                  <td>{course.time}</td>
                  <td>{course.sessions}</td>
                  <td>{course.fee}</td>
                </tr>
              )) : (
                <tr><td colSpan="7">No sessions found</td></tr>
              )}
            </tbody>
          </table>

          <h4>Selected Sessions</h4>
          <table className="styled-table">
            <thead><tr><th>Subject</th><th>From</th><th>To</th><th>Time</th><th>Sessions</th><th>Fee</th></tr></thead>
            <tbody>
              {selectedSessions.map((s, i) => (
                <tr key={i}>
                  <td>{s.subject}</td>
                  <td>{s.from}</td>
                  <td>{s.to}</td>
                  <td>{s.time}</td>
                  <td>{s.sessions}</td>
                  <td>{s.fee}</td>
                </tr>
              ))}
            </tbody>
          </table>

          <button className="next-btn" onClick={nextStep}>NEXT</button>
        </div>
      )}

      {/* ✅ Step 2: Student Details */}
      {step === 2 && (
        <div className="step-content">
          <h2>Personal & Student Details</h2>
          <form>
            <div className="form-grid">
              <input required placeholder="Email" name="email" value={formData.email} onChange={handleInputChange} />
              <input required placeholder="First Name" />
              <input required placeholder="Last Name" />
              <input required placeholder="Address" />
              <input required placeholder="Mobile" />
            </div>
          </form>
          <button className="next-btn" onClick={nextStep}>NEXT</button>
        </div>
      )}

      {step === 3 && (
        <div className="step-content">
          <h2>Confirm Booking & Payment</h2>
          
          {/* Booking Summary */}
          <div className="booking-summary" style={{ marginBottom: '20px', padding: '15px', border: '1px solid #ddd', borderRadius: '5px' }}>
            <h4>Booking Summary</h4>
            {selectedSessions.map((session, index) => (
              <div key={index}>
                <p><strong>{session.subject}</strong> - {session.from} to {session.to}</p>
                <p>Time: {session.time} | Sessions: {session.sessions}</p>
              </div>
            ))}
            <div style={{ marginTop: '10px', borderTop: '1px solid #ddd', paddingTop: '10px' }}>
              <p>Subtotal: ${calculateTotal().subtotal.toFixed(2)}</p>
              <p>Payment Processing Fee (2.9%): ${calculateTotal().surcharge.toFixed(2)}</p>
              <p><strong>Total: ${calculateTotal().total.toFixed(2)}</strong></p>
            </div>
          </div>

          <div style={{ marginBottom: '20px' }}>
            <label style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
              <input 
                type="checkbox" 
                checked={agreed}
                onChange={(e) => setAgreed(e.target.checked)}
                required 
              /> 
              I agree to the Terms and Conditions
            </label>
            <p style={{ fontSize: '12px', marginTop: '10px', color: '#666' }}>
              By proceeding with payment, you also agree to{' '}
              <a 
                href="https://stripe.com/au/legal/consumer" 
                target="_blank" 
                rel="noopener noreferrer"
                style={{ color: '#007bff', textDecoration: 'underline' }}
              >
                Stripe's Terms of Service
              </a>{' '}
              and{' '}
              <a 
                href="https://stripe.com/au/privacy" 
                target="_blank" 
                rel="noopener noreferrer"
                style={{ color: '#007bff', textDecoration: 'underline' }}
              >
                Privacy Policy
              </a>.
              Payment processing includes a 2.9% surcharge as shown above.
            </p>
          </div>

          {agreed && elements && (
            <form onSubmit={handleStripeSubmit} style={{ marginTop: '20px' }}>
              <PaymentElement />
              <div className="btn-group" style={{ marginTop: '20px' }}>
                <button type="button" onClick={prevStep}>BACK</button>
                <button type="submit" disabled={!stripe}>
                  PAY ${calculateTotal().total.toFixed(2)}
                </button>
              </div>
            </form>
          )}

          {!agreed && (
            <div className="btn-group">
              <button type="button" onClick={prevStep}>BACK</button>
            </div>
          )}
        </div>
      )}
    </div>
  );
}
