import React, { useEffect, useState } from 'react';

const curriculumLinks = {
  "Year 1/2": {
    "Hindi": "https://onedrive.live.com/embed?resid=4206D3F9043C3B6A%21306&authkey=!AOxzvoLxxxxxxxx&em=2",
    "Maths and English": "https://onedrive.live.com/embed?resid=4206D3F9043C3B6A%21306&authkey=!AOxzvoLxxxxxxxx&em=2"
  },
  "Year 3": {
    "Maths and English": "https://onedrive.live.com/embed?resid=4206D3F9043C3B6A%21111&authkey=!AOPzxxxxx&em=2"
  },
  "Year 4": {
    "Science": "https://onedrive.live.com/embed?resid=4206D3F9043C3B6A%21222&authkey=!AOZxxxxx&em=2"
  }
};

export default function Curriculum() {
  const [year, setYear] = useState('');
  const [subject, setSubject] = useState('');
  const [link, setLink] = useState('');

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("currentUser")); // { email, year, subject }
    if (user && user.year && user.subject) {
      setYear(user.year);
      setSubject(user.subject);
      const curriculumUrl = curriculumLinks[user.year]?.[user.subject];
      setLink(curriculumUrl || "Not Available");
    }

    const handleContextMenu = (e) => e.preventDefault();
    const handleKeyDown = (e) => {
      if (e.key === "PrintScreen" || (e.ctrlKey && e.key.toLowerCase() === "p")) {
        e.preventDefault();
        alert("Screenshots and printing are disabled.");
      }
    };
    document.addEventListener("contextmenu", handleContextMenu);
    document.addEventListener("keydown", handleKeyDown);
    return () => {
      document.removeEventListener("contextmenu", handleContextMenu);
      document.removeEventListener("keydown", handleKeyDown);
    };
  }, []);

  return (
    <div style={{ padding: "2rem" }}>
      <h2>Curriculum Access</h2>
      {link && link !== "Not Available" ? (
        <>
          <p>
            Showing curriculum for <strong>{subject}</strong> in <strong>{year}</strong>:
          </p>
          <iframe
            src={link}
            width="100%"
            height="600px"
            frameBorder="0"
            sandbox="allow-scripts allow-same-origin"
            allow="fullscreen"
            title="Curriculum Viewer"
          ></iframe>
        </>
      ) : (
        <p>No curriculum found for your enrolled course.</p>
      )}
    </div>
  );
}
