import React, { useEffect, useState } from 'react';

const curriculumLinks = {
  "Year 1/2": {
    "Hindi": "https://onedrive.live.com/embed?resid=4206D3F9043C3B6A%21306&authkey=!AOxzvoLxxxxxxxx&em=2",
    "Maths and English": "https://onedrive.live.com/embed?resid=4206D3F9043C3B6A%21306&authkey=!AOxzvoLxxxxxxxx&em=2"
  },
  "Year 3": {
    "Maths and English": "https://acrobat.adobe.com/id/urn:aaid:sc:ap:9ca52b54-90e2-4030-bda3-4d2f5751f773"
  },
  "Year 4": {
    "Science": "https://onedrive.live.com/embed?resid=4206D3F9043C3B6A%21222&authkey=!AOZxxxxx&em=2"
  }
};

export default function Curriculum() {
  const [year, setYear] = useState('');
  const [subject, setSubject] = useState('');
  const [link, setLink] = useState('');
  const [iframeError, setIframeError] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("currentUser"));
    if (user && user.year && user.subject) {
      setYear(user.year);
      setSubject(user.subject);
      const curriculumUrl = curriculumLinks[user.year]?.[user.subject];
      setLink(curriculumUrl || "Not Available");
    }
    setLoading(false);

    const handleContextMenu = (e) => e.preventDefault();
    const handleKeyDown = (e) => {
      if (e.key === "PrintScreen" || (e.ctrlKey && e.key.toLowerCase() === "p")) {
        e.preventDefault();
        alert("Screenshots and printing are disabled.");
      }
    };

    document.addEventListener("contextmenu", handleContextMenu);
    document.addEventListener("keydown", handleKeyDown);

    return () => {
      document.removeEventListener("contextmenu", handleContextMenu);
      document.removeEventListener("keydown", handleKeyDown);
    };
  }, []);

  const isEmbeddable = link?.includes("embed");

  const handleIframeLoad = () => {
    setIframeError(false);
  };

  const handleIframeError = () => {
    setIframeError(true);
  };

  if (loading) {
    return (
      <div style={{ padding: "2rem", textAlign: "center" }}>
        <h2>Loading...</h2>
      </div>
    );
  }

  return (
    <div style={{ padding: "2rem" }}>
      <h2>Curriculum Access</h2>

      {link && link !== "Not Available" ? (
        <>
          <p>
            Showing curriculum for <strong>{subject}</strong> in <strong>{year}</strong>:
          </p>

          {isEmbeddable && !iframeError ? (
            <div style={{ marginTop: "1rem" }}>
              <iframe
                src={link}
                width="100%"
                height="600px"
                frameBorder="0"
                sandbox="allow-scripts allow-same-origin allow-forms allow-popups"
                allow="fullscreen"
                title="Curriculum Viewer"
                onLoad={handleIframeLoad}
                onError={handleIframeError}
                style={{
                  border: "1px solid #ccc",
                  borderRadius: "5px",
                  backgroundColor: "#f9f9f9"
                }}
              />

              <div style={{ marginTop: "1rem", textAlign: "center" }}>
                <p style={{ fontSize: "0.9rem", color: "#666" }}>
                  Having trouble viewing the document above?
                </p>
                <a
                  href={link}
                  target="_blank"
                  rel="noopener noreferrer"
                  style={{
                    display: "inline-block",
                    padding: "8px 16px",
                    backgroundColor: "#0078d4",
                    color: "white",
                    textDecoration: "none",
                    borderRadius: "4px",
                    fontSize: "0.9rem"
                  }}
                >
                  Open in OneDrive
                </a>
              </div>
            </div>
          ) : (
            <div style={{ marginTop: "1rem" }}>
              {iframeError && (
                <div style={{
                  padding: "1rem",
                  backgroundColor: "#fff3cd",
                  border: "1px solid #ffeaa7",
                  borderRadius: "5px",
                  marginBottom: "1rem"
                }}>
                  <p style={{ margin: 0, color: "#856404" }}>
                    ‚ö†Ô∏è The document couldn't be displayed directly. Please use the link below to access it.
                  </p>
                </div>
              )}

              <a
                href={link}
                target="_blank"
                rel="noopener noreferrer"
                style={{
                  display: "inline-block",
                  padding: "12px 24px",
                  backgroundColor: "#001F42",
                  color: "white",
                  textDecoration: "none",
                  borderRadius: "5px",
                  fontSize: "1rem",
                  transition: "background-color 0.3s"
                }}
                onMouseOver={(e) => e.target.style.backgroundColor = "#003366"}
                onMouseOut={(e) => e.target.style.backgroundColor = "#001F42"}
              >
                üìÑ Open Curriculum in OneDrive
              </a>

              <p style={{
                marginTop: "1rem",
                fontSize: "0.9rem",
                color: "#666",
                fontStyle: "italic"
              }}>
                This will open the curriculum document in a new tab
              </p>
            </div>
          )}
        </>
      ) : (
        <div style={{
          padding: "2rem",
          textAlign: "center",
          backgroundColor: "#f8f9fa",
          borderRadius: "8px",
          border: "1px solid #e9ecef"
        }}>
          <p style={{
            fontSize: "1.1rem",
            color: "#6c757d",
            margin: 0
          }}>
            üìö No curriculum found for your enrolled course.
          </p>
          <p style={{
            fontSize: "0.9rem",
            color: "#6c757d",
            marginTop: "0.5rem"
          }}>
            Please contact your instructor if you believe this is an error.
          </p>
        </div>
      )}
    </div>
  );
}
