import React, { useEffect, useState } from 'react';

const curriculumLinks = {
  "Year 1/2": {
    "Hindi": "https://onedrive.live.com/embed?resid=4206D3F9043C3B6A!306&authkey=!AOxzvoLxxxxxxxx&em=2",
    "Maths and English": "https://onedrive.live.com/embed?resid=4206D3F9043C3B6A!306&authkey=!AOxzvoLxxxxxxxx&em=2"
  },
  "Year 3": {
    "Maths and English": "https://onedrive.live.com/embed?resid=4206D3F9043C3B6A!306&authkey=!AOxzvoLxxxxxxxx&em=2"
  },
  "Year 4": {
    "Science": "https://onedrive.live.com/embed?resid=4206D3F9043C3B6A!222&authkey=!AOZxxxxx&em=2"
  }
};

export default function Curriculum() {
  const [year, setYear] = useState('');
  const [subject, setSubject] = useState('');
  const [link, setLink] = useState('');
  const [iframeError, setIframeError] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("currentUser"));
    if (user && user.year && user.subject) {
      setYear(user.year);
      setSubject(user.subject);
      const curriculumUrl = curriculumLinks[user.year]?.[user.subject];
      setLink(curriculumUrl || "Not Available");
    }
    setLoading(false);

    // Enhanced screenshot and copy protection
    const handleContextMenu = (e) => e.preventDefault();
    
    const handleKeyDown = (e) => {
      if (
        e.key === "PrintScreen" ||
        (e.ctrlKey && e.key.toLowerCase() === "p") ||
        (e.ctrlKey && e.key.toLowerCase() === "s") ||
        (e.ctrlKey && e.shiftKey && e.key.toLowerCase() === "i") ||
        (e.ctrlKey && e.shiftKey && e.key.toLowerCase() === "j") ||
        (e.ctrlKey && e.shiftKey && e.key.toLowerCase() === "c") ||
        (e.ctrlKey && e.key.toLowerCase() === "u") ||
        (e.key === "F12")
      ) {
        e.preventDefault();
        alert("This content is protected and cannot be printed, saved, or captured.");
      }
    };

    const handleDragStart = (e) => e.preventDefault();
    const handleSelectStart = (e) => e.preventDefault();

    const handleBlur = () => {
      document.body.style.filter = 'blur(5px)';
    };
    
    const handleFocus = () => {
      document.body.style.filter = 'none';
    };

    const handleVisibilityChange = () => {
      if (document.hidden) {
        document.body.style.filter = 'blur(5px)';
      } else {
        document.body.style.filter = 'none';
      }
    };

    document.addEventListener("contextmenu", handleContextMenu);
    document.addEventListener("keydown", handleKeyDown);
    document.addEventListener("dragstart", handleDragStart);
    document.addEventListener("selectstart", handleSelectStart);
    document.addEventListener("visibilitychange", handleVisibilityChange);
    window.addEventListener("blur", handleBlur);
    window.addEventListener("focus", handleFocus);

    document.body.style.userSelect = 'none';
    document.body.style.webkitUserSelect = 'none';
    document.body.style.mozUserSelect = 'none';
    document.body.style.msUserSelect = 'none';

    return () => {
      document.removeEventListener("contextmenu", handleContextMenu);
      document.removeEventListener("keydown", handleKeyDown);
      document.removeEventListener("dragstart", handleDragStart);
      document.removeEventListener("selectstart", handleSelectStart);
      document.removeEventListener("visibilitychange", handleVisibilityChange);
      window.removeEventListener("blur", handleBlur);
      window.removeEventListener("focus", handleFocus);
      
      document.body.style.userSelect = '';
      document.body.style.webkitUserSelect = '';
      document.body.style.mozUserSelect = '';
      document.body.style.msUserSelect = '';
      document.body.style.filter = 'none';
    };
  }, []);

  const isEmbeddable = link?.includes("embed");

  const handleIframeLoad = () => {
    setIframeError(false);
  };

  const handleIframeError = () => {
    setIframeError(true);
  };

  // Function to open the same embed link in a new tab
  const openPdf = () => {
    if (link && link !== "Not Available") {
      // Open the same embed link that works in the iframe
      window.open(link, '_blank', 'noopener,noreferrer');
    }
  };

  if (loading) {
    return (
      <div style={{ padding: "2rem", textAlign: "center" }}>
        <h2>Loading...</h2>
      </div>
    );
  }

  return (
    <div style={{ 
      padding: "2rem",
      userSelect: "none",
      webkitUserSelect: "none",
      mozUserSelect: "none",
      msUserSelect: "none"
    }}>
      <h2>Curriculum Access</h2>

      {link && link !== "Not Available" ? (
        <>
          <p>
            Showing curriculum for <strong>{subject}</strong> in <strong>{year}</strong>:
          </p>

          {isEmbeddable && !iframeError ? (
            <div style={{ marginTop: "1rem" }}>
              <iframe
                src={link}
                width="100%"
                height="600px"
                frameBorder="0"
                sandbox="allow-scripts allow-same-origin"
                allow="fullscreen"
                title="Curriculum Viewer"
                onLoad={handleIframeLoad}
                onError={handleIframeError}
                style={{
                  border: "1px solid #ccc",
                  borderRadius: "5px",
                  backgroundColor: "#f9f9f9",
                  pointerEvents: "auto",
                  userSelect: "none"
                }}
              />

              <div style={{ marginTop: "1rem", textAlign: "center" }}>
                <p style={{ fontSize: "0.9rem", color: "#666" }}>
                  Need to open in a new tab?
                </p>
                <button
                  onClick={openPdf}
                  style={{
                    display: "inline-block",
                    padding: "8px 16px",
                    backgroundColor: "#0078d4",
                    color: "white",
                    border: "none",
                    borderRadius: "4px",
                    fontSize: "0.9rem",
                    cursor: "pointer"
                  }}
                >
                  📄 Open PDF
                </button>
              </div>
            </div>
          ) : (
            <div style={{ marginTop: "1rem" }}>
              {iframeError && (
                <div style={{
                  padding: "1rem",
                  backgroundColor: "#fff3cd",
                  border: "1px solid #ffeaa7",
                  borderRadius: "5px",
                  marginBottom: "1rem"
                }}>
                  <p style={{ margin: 0, color: "#856404" }}>
                    ⚠️ The document couldn't be displayed directly. Please use the link below to access it.
                  </p>
                </div>
              )}

              <button
                onClick={openPdf}
                style={{
                  display: "inline-block",
                  padding: "12px 24px",
                  backgroundColor: "#001F42",
                  color: "white",
                  border: "none",
                  borderRadius: "5px",
                  fontSize: "1rem",
                  transition: "background-color 0.3s",
                  cursor: "pointer"
                }}
                onMouseOver={(e) => e.target.style.backgroundColor = "#003366"}
                onMouseOut={(e) => e.target.style.backgroundColor = "#001F42"}
              >
                📄 Worksheets.pdf
              </button>

              <p style={{
                marginTop: "1rem",
                fontSize: "0.9rem",
                color: "#666",
                fontStyle: "italic"
              }}>
                Click to open the curriculum document
              </p>
            </div>
          )}
        </>
      ) : (
        <div style={{
          padding: "2rem",
          textAlign: "center",
          backgroundColor: "#f8f9fa",
          borderRadius: "8px",
          border: "1px solid #e9ecef"
        }}>
          <p style={{
            fontSize: "1.1rem",
            color: "#6c757d",
            margin: 0
          }}>
            📚 No curriculum found for your enrolled course.
          </p>
          <p style={{
            fontSize: "0.9rem",
            color: "#6c757d",
            marginTop: "0.5rem"
          }}>
            Please contact your instructor if you believe this is an error.
          </p>
        </div>
      )}
    </div>
  );
}
