// src/pages/Payment/StripeWrapper.jsx
import React, { useEffect, useState } from 'react';
import { Elements } from '@stripe/react-stripe-js';
import { loadStripe } from '@stripe/stripe-js';
import BookingPage from '../Booking';

const stripePromise = loadStripe('pk_live_51Otnc3S6jivUUFNCjV9JH1ZzVB3xtTKoGOviUhuGHoCGrQl3C8EYyctta6OgM1GGIHLBDi5weeKg9KYdbc3TjyQ800ST0dEpOP'); // your publishable key

export default function StripeWrapper() {
  const [clientSecret, setClientSecret] = useState('');

  useEffect(() => {
    // TEMP amount just for example (update later to be dynamic)
    fetch('http://localhost:5000/api/create-payment-intent', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ amount: 1000 }) // example: $10.00
    })
      .then(res => res.json())
      .then(data => setClientSecret(data.clientSecret));
  }, []);

  if (!clientSecret) return <p>Loading payment...</p>;

  return (
    <Elements options={{ clientSecret }} stripe={stripePromise}>
      <BookingPage />
    </Elements>
  );
}
