import React, { useState, useEffect } from 'react';
import './Navbar.css';

export default function Navbar() {
  const [showNavbar, setShowNavbar] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setShowNavbar(window.scrollY > 100);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const scrollToSection = (id) => {
    const section = document.getElementById(id);
    if (section) {
      section.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <nav className={`main-navbar ${showNavbar ? 'sticky' : ''}`}>
      <div className="nav-content">
        <img src="/assets/logo-black.png" alt="MathSciLab" className="nav-logo" />
        <ul>
          <li><button onClick={() => scrollToSection('about')}>About</button></li>
          <li><button onClick={() => scrollToSection('courses')}>Courses</button></li>
          <li><button onClick={() => scrollToSection('enroll')}>Enroll</button></li>
          <li><button onClick={() => scrollToSection('login')}>Login</button></li>
        </ul>
      </div>
    </nav>
  );
}
